import { useColorMode } from "bumbag";
// import React, { useState } from "react"
import { Moon, Sun } from 'react-feather'
import { styles } from '@emotion/react'

const DarkMode = () => {
  const {colorMode, setColorMode} = useColorMode('light')
  const toggle = () => setColorMode(colorMode === 'dark' ? 'default' : 'dark')

  return <button className={css`&:hover {
    color: ${color};
  }
`} onClick={toggle}>{colorMode === 'dark' ? <Sun /> : <Moon />}</button>
};
export default DarkMode;
