import React from "react";
import { Modal, Card, Box, Button } from "bumbag";
import ButtonUI from "./ButtonUI";

const ModalPopup = ({ showModal }) => {
  const { modal } = Modal.useContext();
  return (
    <Modal.State animated visible={showModal}>
      {/* <Modal.Disclosure>Open modal</Modal.Disclosure> */}
      <Modal fade>
        <Card>
          <Box>Thank you for contacting! I will get back to you within the ne</Box>
          <ButtonUI name={"okay"} />
        </Card>
      </Modal>
    </Modal.State>
  );
};

export default ModalPopup;
