import React from "react";
import { projects } from "../projects.json";
import { Link } from "react-router-dom";

import {
  Card,
  Box,
  Button,
  Divider,
  Flex,
  Image,
  Stack,
  Set,
  Heading,
  Container,
  Columns,
  Text,
  Paragraph,
  Badge,
} from "bumbag";

const Project = ({ title, info, link }) => (
  <Box paddingY="major-2" margin='0' display='box'>
    <Columns>

      <Columns.Column spread={4} spreadOffset={4} alignSelf="center">
        <a href={link} rel="noreferrer" target="_blank">
          <Heading
            use="h4"
            fontWeight="semibold"
            paddingTop="1rem"
            paddingBottom="1rem"
            cursor="pointer"
            _hover={{ color: "terciary" }}
          >
            {title}
          </Heading>
        </a>
      </Columns.Column>
      <Columns.Column spread={4}>
        <Paragraph
          paddingTop="1rem"
          paddingBottom="1rem"
          color="secondary"
          lineHeight={{ default: "1.8rem", "max-tablet": "1.2rem" }}
        >
          {info}
        </Paragraph>
      </Columns.Column>
    </Columns>
  </Box>
);

const Projects = () => {
  return (
    <Box>
    <Heading use="h4" marginBottom="0" alignSelf='center' display='box'>
        Projects
      </Heading>
      {projects.map((project) => (
        <Project {...project} />
      ))}
      <Text>You can check all projects on my GitHub&nbsp;</Text>
      <Text _hover={{ color: "terciary" }}>
        <a
          href={"https://github.com/daviavmello"}
          rel="noreferrer"
          target="_blank"
        >
          here.
        </a>
      </Text>
    </Box>
  );
};

export default Projects;
