import { useColorMode } from "bumbag";
import React, { useState } from "react"
import { Moon, Sun } from "react-feather"

const DarkMode = () => {
  const {colorMode, setColorMode} = useColorMode('light')
  const toggle = () => setColorMode(colorMode === 'dark' ? 'default' && <Sun /> : 'dark' )

  const toggleIcon = () => (colorMode === 'dark' ? <Sun /> : <Moon />)

  return <button color='white' onClick={toggle}></button>
};
export default DarkMode;
