import React, { useState, useEffect, useRef } from "react";
import {
  Input,
  Flex,
  Heading,
  Container,
  Columns,
  Textarea,
  Stack,
  Button,
  ActionButtons,
} from "bumbag";
import emailjs from "emailjs-com";

import { Users } from "react-feather";
import ButtonUI from "./ButtonUI";
import { Formik, Form, Field } from "formik";
import ModalPopup from "./ModalPopup";

const Contact = () => {
  const [showModal, setShowModal] = useState(false);
  const ref = useRef(null);

  // useEffect(() => {
  //   if (ref) {
  //     console.log(ref);
  //   }
  // }, []);

  const handleTextArea = (e) => {
    console.log(e.input);
  };

  const sendEmail = (values) => {
    if (!values.name) {
      alert("Please enter your name.");
    }
    if (!values.email) {
      alert("Please enter your email.");
    } else if (
      !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)
    ) {
      alert("Please enter a valid email.");
    }
    if (!values.message) {
      alert("Please enter a message.");
    }

    emailjs
      .send(
        "service_eq4k8nu",
        "template_d7xhn8q",
        values,
        "user_PXcI30CjdFLTla1cHZFoV"
      )
      .then(
        (result) => {
          console.log(result.text);
          setShowModal(true);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <Container padding="2rem 1rem">
      <Flex
        display={{ default: "flex", "max-tablet": "block" }}
        alignX="center"
        alignY="center"
        alignItems="baseline"
      >
        <Columns.Column spread={6}>
          <Heading
            use="h4"
            fontWeight={{ default: "semibold", "max-tablet": "bold" }}
            paddingBottom={{ default: "0rem", "max-tablet": "2rem" }}
          >
            Contact
          </Heading>
        </Columns.Column>
        <Columns.Column spread={6}>
          <Formik
            initialValues={{ name: "", email: "", message: "" }}
            onSubmit={(values) => sendEmail(values)}
          >
            <Form>
              {showModal && <ModalPopup showModal={showModal} />}
              <Stack spacing="major-6">
                <Field
                  component={Input.Formik}
                  variant="underline"
                  name="name"
                  type="text"
                  label="Name"
                  color="contrast"
                  style={{
                    background: "none",
                  }}
                />
                <Field
                  component={Input.Formik}
                  variant="underline"
                  name="email"
                  type="email"
                  label="Email"
                  color="contrast"
                  style={{
                    background: "none",
                  }}
                />
                <Field
                  component={Textarea.Formik}
                  // variant="underline"
                  name="message"
                  type="text"
                  placeholder="Message"
                  spellCheck={true}
                  autoFocus={false}
                  color="contrast"
                  _groupFocus={{
                    outline: "none",
                    border: "0",
                    boxShadow: "none",
                    dropShadow: "none",
                  }}
                  _groupActive={{
                    outline: "none",
                    border: "0",
                    boxShadow: "none",
                  }}
                  style={{
                    // border: "0",
                    // // borderBottom:
                    // //   "1px solid var(--bb-palette-gray700, #000000)",
                    // borderRadius: "0",
                    // background: "none",
                    // padding: "0",
                  }}
                />
                <ButtonUI
                  name={"Send"}
                  value="Send"
                  isSend
                  onClick={(e) => console.log("clicked")}
                />
              </Stack>
            </Form>
          </Formik>
        </Columns.Column>
      </Flex>
    </Container>
  );
};

export default Contact;
