import React from "react";
import {
  Input,
  Flex,
  Heading,
  Container,
  Columns,
  Textarea,
  Stack,
} from "bumbag";
import emailjs from "emailjs-com";

import { Users } from "react-feather";
import ButtonUI from "./ButtonUI";

const Contact = () => {
  const sendEmail = (e) => {
    e.preventDefault();
    console.log(e);
    emailjs
      .sendForm(
        "gmail",
        "template_d7xhn8q",
        e.target,
        "user_PXcI30CjdFLTla1cHZFoV"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <Container padding="2rem 1rem">
      <Flex
        display={{ default: "flex", "max-tablet": "block" }}
        alignX="center"
        alignY="center"
        alignItems="baseline"
      >
        <Columns.Column spread={6}>
          <Heading
            use="h4"
            fontWeight={{ default: "semibold", "max-tablet": "bold" }}
            paddingBottom={{ default: "0rem", "max-tablet": "2rem" }}
          >
            Contact
          </Heading>
        </Columns.Column>
        <Columns.Column spread={6}>
          <Stack spacing="major-6">
            <Input placeholder="Name" type="text" variant="underline" />
            <Input placeholder="E-mail" type="email" variant="underline" />
            <Textarea
              variant="underline"
              type="text"
              placeholder="Message"
              spellCheck={true}
              style={{
                border: "0",
                borderBottom: "1px solid var(--bb-palette-white900, #000000)",
                borderRadius: "0",
                background: "none",
                padding: "0",
              }}
            />
            {/* <ButtonUI
              name={"Send"}
              value="Send"
              isSend
              onClick={(e) => console.log("clicked")}
            /> */}
            <ButtonUI
              name={"Send"}
              value="Send"
              isSend
              onClick={(e) => console.log("clicked")}
            />
          </Stack>
        </Columns.Column>
      </Flex>
    </Container>
  );
};

export default Contact;
