import React from "react";
// import { Link } from 'react-router-dom'
import { Minus } from "react-feather";
import ScrollDown from "./ScrollDown";
import {
  Card,
  Box,
  Button,
  Image,
  Stack,
  Set,
  Heading,
  Container,
  Columns,
  Text,
  Paragraph,
  Badge,
} from "bumbag";

const Header = () => (
  <Columns margin="0">
    <Columns.Column
      spreadTablet={12}
      spreadDesktop={6}
      textAlign="center"
      alignSelf="center"
      justifyContent="center"
      display="flex"
    >
      <Box
        textAlign="left"
        paddingX={{ default: "major-6", "max-tablet": "major-2" }}
        marginY={{ default: "major-12", "max-tablet": "major-10" }}
      >
        <Text
          use="h3"
          fontWeight="bold"
          color="terciary"
          marginBottom="1rem"
          display="flex"
        >
          <Minus />
          &nbsp;Davi Mello
        </Text>{" "}
        <Heading
          use="h2"
          marginBottom={{ default: "major-10", "max-tablet": "major-6" }}
          lineHeight={{ default: "3rem", "max-tablet": "2.4rem" }}
        >
          Hello there!
          <span role="img" aria-label="emojis">
            {" "}
            ðŸ‘‹
          </span>{" "}
          <br />
          I'm a Software Engineer.
        </Heading>
        <ScrollDown />
      </Box>
    </Columns.Column>

    <Columns.Column
      spreadMobile={0}
      spreadDesktop={6}
      padding="0"
      display={{ default: "block", "max-tablet": "none" }}
    >
      <Image
        src={`/img/profile.JPG`}
        alt=""
        fit="cover"
        width="fit-content"
        maxHeight="45rem"
      />
    </Columns.Column>
  </Columns>
);

export default Header;
