import React, { useState } from "react";
import { Modal, Card, Box, Button, Heading } from "bumbag";
import ButtonUI from "./ButtonUI";

const ModalPopup = ({ showModal }) => {
  return (
    <Modal.State animated visible={true}>
      <Modal fade>
        <Card>
          <Box>
            <Heading
              use="h4"
              fontWeight={{ default: "semibold", "max-tablet": "bold" }}
              paddingBottom={{ default: "0rem", "max-tablet": "2rem" }}
              line
            >
              Thank you for contacting me!
              <br />I will get back to you within the next 48 hours.
            </Heading>
          </Box>
          <Modal.Disclosure>
            {(modalDisclosureProps) => (
              <Button
                fontWeight="bold"
                borderRadius="7"
                border="none"
                textTransform="uppercase"
                color="contrast"
                backgroundColor="primary"
                {...modalDisclosureProps}
              >
                Okay
              </Button>
            )}
          </Modal.Disclosure>
        </Card>
      </Modal>
    </Modal.State>
  );
};

export default ModalPopup;
