import React from "react";
import { projects } from "../projects.json";
import { Link } from "react-router-dom";

import {
  Card,
  Box,
  Button,
  Divider,
  Flex,
  Image,
  Stack,
  Set,
  Heading,
  Container,
  Columns,
  Text,
  Paragraph,
  Badge,
} from "bumbag";

const Title= () => (
<Columns>
      <Columns.Column spread={4} position='sticky' padding='0'>
    <Heading use="h4" >
        Projects
      </Heading>
      </Columns.Column>
      </Columns>
)

const Project = ({ title, info, link }) => (
  // <Box alignY="top"> 
  <Columns> 

  <Columns.Column spread={4} spreadOffset='left' display='block' padding='0'>
        <a href={link} rel="noreferrer" target="_blank">
          <Heading
            use="h4"
            fontWeight="semibold"
            // paddingTop="1rem"
            paddingBottom="4rem"
            cursor="pointer"
            _hover={{ color: "terciary" }}
          >
            {title}
          </Heading>
        </a>
        </Columns.Column>

      <Columns.Column spread={4} padding='0'>
        <Paragraph
          // paddingTop="1rem"
          paddingBottom="4rem"
          color="secondary"
          lineHeight={{ default: "1.8rem", "max-tablet": "1.2rem" }}
        >
          {info}
        </Paragraph>
        </Columns.Column>
        </Columns>
  //  </Box> 
);

const Projects = () => {
  return (
    <Container padding="2rem 1rem">
      {/* <Box display='block' paddingBottom='2rem'> */}

      {/* </Box> */}
      {projects.map((project) => (
        <Project {...project} />
        ))}

      <Box display='block'>  
      <Text>You can check all projects on my GitHub&nbsp;</Text>
      <Text _hover={{ color: "terciary" }}>
        <a
          href={"https://github.com/daviavmello"}
          rel="noreferrer"
          target="_blank"
          >
          here.
        </a>
      </Text>
      </Box>
    </Container>
  );
};

export default Projects;
