import React from "react";
import { Button } from "bumbag";
import { Send, Icon } from "react-feather";

const ButtonUI = ({ name, isSend }) => (
  <Button
    use="h3"
    fontWeight="bold"
    borderRadius="7"
    border="none"
    textTransform="uppercase"
    color="contrast"
    backgroundColor="primary"
  >
    {isSend && <Icon aria-label="Igloo" icon={<Send />} fontSize="100" />}
    {name}
  </Button>
);

export default ButtonUI;
