import React from "react";
import { projects } from "../projects.json";
import { Link } from "react-router-dom";

import {
  Card,
  Box,
  Button,
  Divider,
  Flex,
  Image,
  Stack,
  Set,
  Heading,
  Container,
  Columns,
  Text,
  Paragraph,
  Badge,
} from "bumbag";

const Project = ({ folder, title, info, link }) => (
  <Stack orientation='horizontal'>
  {/* <Columns.Column spread={4}> */}
    <Heading use="h4" margin='0' fontWeight="semibold" >
            {folder}
    </Heading>
  {/* </Columns.Column>
  <Columns.Column spread={4}> */}
        <a href={link} rel="noreferrer" target="_blank">
          <Heading
            use="h4"
            fontWeight="semibold"
            paddingTop='0'
            paddingBottom="4rem"
            cursor="pointer"
            _hover={{ color: "terciary" }}
          >
            {title}
          </Heading>
        </a>
        {/* </Columns.Column>

      <Columns.Column spread={4}> */}
        <Paragraph
          paddingBottom="4rem"
          color="secondary"
          lineHeight={{ default: "1.8rem", "max-tablet": "1.2rem" }}
        >
          {info}
        </Paragraph>
        {/* </Columns.Column> */}
        </Stack>
);

const Projects = () => {
  return (
    <Container padding="2rem 1rem"> 
      {projects.map((project) => (
        <Project {...project} />
        ))}

      <Box display='block'>  
      <Text>You can check all projects on my GitHub&nbsp;</Text>
      <Text _hover={{ color: "terciary" }}>
        <a
          href={"https://github.com/daviavmello"}
          rel="noreferrer"
          target="_blank"
          >
          here.
        </a>
      </Text>
      </Box>
    </Container>
  );
};

export default Projects;
