import React from "react";
import {
  Input,
  Flex,
  Heading,
  Container,
  Columns,
  Textarea,
  Stack,
} from "bumbag";
import emailjs from "emailjs-com";

import { Users } from "react-feather";
import ButtonUI from "./ButtonUI";

const Contact = () => {
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", e.target, "YOUR_USER_ID")
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <Container padding="2rem 1rem">
      <Flex
        display={{ default: "flex", "max-tablet": "block" }}
        alignX="center"
        alignY="center"
        alignItems="baseline"
      >
        <Columns.Column spread={6}>
          <Heading
            use="h4"
            fontWeight={{ default: "semibold", "max-tablet": "bold" }}
            paddingBottom={{ default: "0rem", "max-tablet": "2rem" }}
          >
            Contact
          </Heading>
        </Columns.Column>
        <Columns.Column spread={6}>
          <form onSubmit={sendEmail}>
            <Stack spacing="major-6">
              <Input placeholder="Name" type="text" variant="underline" />
              <Input placeholder="E-mail" type="email" variant="underline" />
              <Textarea
                variant="underline"
                type="text"
                placeholder="Message"
                spellCheck={true}
                style={{
                  border: "0",
                  borderBottom: "1px solid var(--bb-palette-white900, #000000)",
                  borderRadius: "0",
                  background: "none",
                  padding: "0",
                }}
              />
              <ButtonUI name={"Send"} isSend />
            </Stack>
          </form>
        </Columns.Column>
      </Flex>
    </Container>
  );
};

export default Contact;
