import React, { useState } from "react";
import { Modal, Card, Box, Button, Heading } from "bumbag";
import ButtonUI from "./ButtonUI";

const ModalPopup = ({ showModal }) => {
  const { modal } = Modal.useContext();
  const [openModal, setOpenModal] = useState(showModal);
  return (
    <Modal.State animated visible={openModal}>
      <Modal fade>
        <Card>
          <Box>
            <Heading
              use="h4"
              fontWeight={{ default: "semibold", "max-tablet": "bold" }}
              paddingBottom={{ default: "0rem", "max-tablet": "2rem" }}
            >
              Thank you for contacting me!
              <br />I will get back to you within the next 48 hours.
            </Heading>
          </Box>
          {/* <Modal.Disclosure use={Button}>Close modal</Modal.Disclosure> */}
          <Button onClick={(e) => setOpenModal(false)}>Asa</Button>
        </Card>
      </Modal>
    </Modal.State>
  );
};

export default ModalPopup;
