import React from "react";
import { projects } from "../projects.json";
import { Link } from "react-router-dom";

import {
  Card,
  Box,
  Button,
  Divider,
  Image,
  Stack,
  Set,
  Heading,
  Container,
  Columns,
  Text,
  Paragraph,
  Badge,
} from "bumbag";

const Project = ({ title, info }) => (
  <Container padding="2rem 1rem">
    <Set alignItems="baseline">
      <Columns>
      <Columns.Column spread={6}>
      <Heading
        use="h4"
        fontWeight="semibold"
        paddingTop="1.5rem"
        paddingBottom="1.5rem"
      >
        {title}
      </Heading>
      </Columns.Column>
      <Columns.Column spread={6}>
      <Paragraph
        paddingTop="1.5rem"
        paddingBottom="1.5rem"
        color="secondary"
        justifyContent="end"
      >
        {info}
      </Paragraph>
      </Columns.Column>
      <Divider />
      </Columns>
    </Set>
  </Container>
);

const Projects = () => {
  return (
    <Set>
      <Heading use="h4" marginBottom="major-4">
        Projects
      </Heading>
      {projects.map((project) => (
        <Project {...project} />
      ))}
    </Set>
  );
};

export default Projects;
