import { useColorMode } from "bumbag";
import React, { useState } from "react";
import { Moon, Sun } from "react-feather";

const DarkMode = () => {
  const [colorMode, setColorMode] = useState(Moon);
  const toggle = () => setColorMode(Sun);

  return <div color="#5863D8" onClick={colorMode}></div>;
};
export default DarkMode;
